$msgConfigsTplKeys = array_keys($msgConfigsTpl);


$tables = db_factory::query("SHOW TABLES LIKE '".TABLEPRE."witkey_msg_tpl'");
if($tables[0]){
	$msgLists = db_factory::query("SELECT * FROM `".TABLEPRE."witkey_msg_tpl` WHERE send_type != '2'   ORDER BY tpl_code ASC");
	if($msgLists){
		foreach ($msgLists as $k=>$v){
			$sql  = "UPDATE `".TABLEPRE."witkey_msg_config` SET `send_type`='{$v['send_type']}' ,`content`='{$v['content']}'  WHERE k='{$v['tpl_code']}'";
			db_factory::execute($sql);
		}
	}
}


$smsLists = db_factory::get_table_data('*', 'witkey_msg_config', null, ' k asc ',null, null, 'k', 0);

$smsListsKeys = array_keys($smsLists);
if ($smsLists){
	foreach ($smsLists as $k =>$v){
		if(in_array($k, $msgConfigsTplKeys)){
			$setsqlarr = array();
			$setsqlarr['content'] = $v['content']?str_replace(array('<p></p>','<p><br/></p>','<p><br /></p>'), array('','',''), htmlspecialchars_decode($v['content'])):'';
			$setsqlarr['content'] = $v['content']?str_replace(array('task_id='), array('id='), $v['content']):$v['content'];
			$setsqlarr['mobile_content'] = $v['mobile_content']?str_replace(array('<p></p>','<p><br/></p>','<p><br /></p>'), array('','',''), htmlspecialchars_decode($v['mobile_content'])):'';
			if(!$setsqlarr['content']){
				$setsqlarr['content'] = $msgConfigsTpl[$k]['content'];
			}
			if(!$setsqlarr['mobile_content']){
				$setsqlarr['mobile_content'] = $msgConfigsTpl[$k]['mobile_content'];
			}
			$wheresqlarr = array();
			$wheresqlarr['config_id'] = $v['config_id'];
			db_factory::updatetable(TABLEPRE.'witkey_msg_config', $setsqlarr, $wheresqlarr);
		}
	}
}


if($msgConfigsTpl){
	foreach ($msgConfigsTpl as $k=>$v){
		$insertsqlarr = array();
		
		$insertsqlarr['content'] = $msgConfigsTpl[$k]['content']?str_replace(array('<p></p>','<p><br/></p>','<p><br /></p>'), array('','',''), htmlspecialchars_decode($msgConfigsTpl[$k]['content'])):'';
		$insertsqlarr['content'] = $msgConfigsTpl[$k]['content']?str_replace(array('task_id='), array('id='), $msgConfigsTpl[$k]['content']):'';
		
		if($k =='get_password'){
			$insertsqlarr['mobile_content'] = $insertsqlarr['content']
			?str_replace(array('<p></p>','<p><br/></p>','<p><br /></p>'), array('','',''), htmlspecialchars_decode($insertsqlarr['content'])):
			$insertsqlarr['content'];
		}else{
			$insertsqlarr['mobile_content'] = $msgConfigsTpl[$k]['mobile_content']
												?str_replace(array('<p></p>','<p><br/></p>','<p><br /></p>'), array('','',''), htmlspecialchars_decode($msgConfigsTpl[$k]['mobile_content'])):
											$insertsqlarr['content'];
		}
		
		

		
		
		$insertsqlarr['k'] = $msgConfigsTpl[$k]['k'];
		$insertsqlarr['obj'] = $msgConfigsTpl[$k]['obj'];
		$insertsqlarr['desc'] = $msgConfigsTpl[$k]['desc'];
		$insertsqlarr['prompt'] = $msgConfigsTpl[$k]['prompt'];
		$insertsqlarr['v'] = 'a:2:{s:8:"send_sms";i:1;s:10:"send_email";i:1;}';
		$insertsqlarr['on_time'] = $msgConfigsTpl[$k]['on_time'];
		//$insertsqlarr['content'] = $msgConfigsTpl[$k]['content'];
		$insertsqlarr['send_type'] = $msgConfigsTpl[$k]['send_type'];
		//$insertsqlarr['mobile_content'] = $msgConfigsTpl[$k]['mobile_content'];
		$insertsqlarr['mobile'] = 0;
		if(!in_array($k, $smsListsKeys)){

			db_factory::inserttable(TABLEPRE.'witkey_msg_config', $insertsqlarr);
		}else{
			$wheresqlarr = array();
			$wheresqlarr['k'] = $k;
			db_factory::updatetable(TABLEPRE.'witkey_msg_config', $insertsqlarr, $wheresqlarr);
			
		}
	}
}



